package com.github.muellerc.techforum2016.jboss.image;

import javax.ejb.ActivationConfigProperty;
import javax.ejb.MessageDriven;
import javax.inject.Inject;
import javax.jms.*;

@MessageDriven(name = "IMAGEQueueMDB", activationConfig = {
        @ActivationConfigProperty(propertyName = "destinationLookup", propertyValue = "queue/IMAGE"),
        @ActivationConfigProperty(propertyName = "destinationType", propertyValue = "javax.jms.Queue"),
        @ActivationConfigProperty(propertyName = "acknowledgeMode", propertyValue = "Auto-acknowledge") })
public class ImageServiceMessageListener implements MessageListener {

    @Inject
    JMSContext jms;

    public void onMessage(Message message) {
        try {
            Destination replyTo = message.getJMSReplyTo();
            if (replyTo == null) {
                return;
            }

            TextMessage request = (TextMessage) message;
            String payload = request.getText();
            System.out.println("Got request: " + payload);

            jms.createProducer().send(replyTo, "data:image/png;base64,");
        } catch (JMSException e) {
            throw new RuntimeException(e);
        }
    }
}
